<?php require_once __DIR__.'/conexion.php'; $res = $conn->query('SELECT codigo,nombre,creditos FROM asignaturas ORDER BY id'); ?>
<!doctype html><html><head><meta charset='utf-8'><title>Export Asignaturas</title></head><body><h2>Asignaturas</h2><table border=1 cellpadding=6><tr><th>Codigo</th><th>Nombre</th><th>Creditos</th></tr><?php while($r=$res->fetch_assoc()){ ?><tr><td><?php echo htmlspecialchars($r['codigo']);?></td><td><?php echo htmlspecialchars($r['nombre']);?></td><td><?php echo $r['creditos'];?></td></tr><?php } ?></table><script>window.print();</script></body></html>